mapper.select:
SELECT a.seq ,a.name ,a.CodeGroupNameEN ,a.CodeGroupRegDate ,a.CodeGroupModDate ,(select count(aa.seq) from Code aa where 1=1 and aa.DelNy = 0 and aa.cg_seq = a.seq) as seqCount ,a.DelNy FROM CodeGroup a WHERE 1=1
AND DelNy = #{shDelNy}
AND seq = #{shValue} AND name LIKE CONCAT('%',#{shValue},'%') AND CodeGroupNameEN LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO CodeGroup (
	seq
	,name
	,CodeGroupNameEN
	,DelNy
	)
VALUES(
	#{seq}
	,#{name}
	,#{CodeGroupNameEN}
	,#{DelNy }
	)

		SELECT last_insert_id()

mapper.update:
UPDATE CodeGroup
SET
	seq = #{seq}
	,name = #{name}
	,CodeGroupNameEN = #{CodeGroupNameEN}

WHERE
	seq = #{seq}
