mapper.insert:
INSERT INTO memberAddr(
	member_seq
	,div_addr
	,addr_defaultNy
	,addr
	,addr_detail
	,zip
	,DelNy
	,memberAddrLat
	,memberAddrLng
	)
	VALUES(
	#{member_seq }
	,#{div_addr }
	,#{addr_defaultNy }
	,#{addr }
	,#{addr_detail }
	,#{zip }
	,#{DelNy }
	,#{memberAddrLat }
	,#{memberAddrLng }
	)

	SELECT last_insert_id()

mapper.update:
UPDATE memberAddr
SET
	member_seq = #{member_seq }
	,div_addr = #{div_addr }
	,addr_defaultNy = #{addr_defaultNy }
	,addr = #{addr }
	,addr_detail = #{addr_detail }
	,zip = #{zip }
	,DelNy = #{DelNy }
	,memberAddrLat = #{memberAddrLat }
	,memberAddrLng = #{memberAddrLng }
WHERE maSeq = #{maSeq}
