mapper.insert:
INSERT INTO memberCard(
	seq
	,member_seq
	,div_memberCard
	,card_defaultNy
	,card
	,DelNy
	)
VALUES(
	#{seq }
	,#{member_seq }
	,#{div_memberCard }
	,#{card_defaultNy }
	,#{card }
	,#{DelNy }
	)

	SELECT last_insert_id()

mapper.update:
UPDATE memberCard
SET
seq = #{seq }
,member_seq = #{member_seq }
,div_memberCard = #{div_memberCard }
,card_defaultNy = #{card_defaultNy }
,card = #{card }
WHERE seq = #{seq}>
