mapper.insert:
INSERT INTO memberTel(
	seq
	,member_seq
	,div_tel
	,tel_defaultNy
	,tel
	,tel_company_code
	,DelNy
	)
VALUES(
	#{seq }
	,#{member_seq }
	,#{div_tel }
	,#{tel_defaultNy }
	,#{tel }
	,#{tel_company_code }
	,#{DelNy }
	)

	SELECT last_insert_id()

mapper.update:
UPDATE memberTel
SET
seq = #{seq }
,member_seq = #{member_seq }
,div_tel = #{div_tel }
,tel_defaultNy = #{tel_defaultNy }
,tel = #{tel }
,tel_company_code = #{tel_company_code }
WHERE seq = #{seq}>
