mapper.insert:
INSERT INTO productQA(
	qaSeq
	,title
	,question
	,qaRegtime
	,answerNY
	,productSeq
	,memberSeq
	,DelNy
	)
VALUES(
	#{qaSeq }
	,#{title }
	,#{question }
	,NOW()
	,0
	,#{productSeq }
	,#{memberSeq }
	,0
	)

SELECT last_insert_id()

mapper.update:
UPDATE productQA
	SET
	qaSeq = {#qaSeq }
	,title = {#title }
	,question = {#question }
	,qaRegtime = {#qaRegtime }
	,answerNY = {#answerNY }
	,productSeq = {#productSeq }
	,memberSeq = {#memberSeq }
	,DelNy = {#DelNy }
WHERE qaSeq = {#qaSeq}
